$(document).ready(function(){$("#confirmModal").on("hide.bs.modal",function(event){window.location.reload(true)});$("#delTargetModal").on("show.bs.modal",function(event){relTarget=$(event.relatedTarget);var prom=relTarget.data("valprom");var job=relTarget.data("valjob");var labels=relTarget.data("vallabels");var targets=relTarget.data("valtarget");var modal=$(this);modal.find("#delTargetProm").val(prom);modal.find("#delTargetJob").val(job);modal.find("#delTargetLabels").val(labels);modal.find("#delTargetTarget").val(targets)});$("#addTargetsModal").on("show.bs.modal",function(event){var button=$(event.relatedTarget);var prom=button.data("valprom");var job=button.data("valjob");var labels=button.data("vallabels");var modal=$(this);modal.find("#addTargetProm").val(prom);modal.find("#addTargetJob").val(job);modal.find("#addTargetLabels").val(labels)});$("#confirmDelTarget").click(function(){var paras={prom:$("#delTargetProm").val(),job:$("#delTargetJob").val(),labels:$("#delTargetLabels").val(),targets:$("#delTargetTarget").val()};$.ajax({type:"post",url:"/prom/api/v1/del/targets",data:JSON.stringify(paras),dataType:"json",contentType:"application/json,charset=utf-8",success:function(resp,status){$("#delTargetModal").modal("toggle");$("#confirmModal").find("#confirmMessage").text(resp.msg);$("#confirmModal").modal("show")}})});$("#confirmAddTargets").click(function(){var paras={prom:$("#addTargetProm").val(),job:$("#addTargetJob").val(),labels:$("#addTargetLabels").val(),targets:$("#addTargetTargets").val()};$.ajax({type:"post",url:"/prom/api/v1/add/targets",data:JSON.stringify(paras),dataType:"json",contentType:"application/json,charset=utf-8",success:function(resp,status){$("#addTargetsModal").modal("toggle");$("#confirmModal").find("#confirmMessage").text(resp.msg);$("#confirmModal").modal("show")}})})});
